mapper.select:
select b.*
from(select rownum rn, a.*
    from(
        select myroom_next_id myroomNextId, myroom_id myroomId, user_id, subject, content, reg_date, update_date, is_share isShare
        from myroom_next
        where user_id=#{user_id}
        order by update_date desc
        ) a
    where rownum <= #{end}
    ) b
where b.rn > #{start}

select b.*
from(select rownum rn, a.*
    from(
        select myroom_next_id myroomNextId, myroom_id myroomId, user_id, subject, content, reg_date, update_date, is_share isShare
        from myroom_next
        where user_id=#{user_id} and myroom_id=#{myroomId}
        order by update_date desc
        ) a
    where rownum <= #{end}
    ) b
where b.rn > #{start}

select b.*
from(select rownum rn, a.*
    from(
        select myroom_next_id myroomNextId, myroom_id myroomId, user_id, subject, content, reg_date, update_date, is_share isShare
        from myroom_next
        where user_id=#{user_id}
    
        and content like '%' || #{word} ||'%'
    
    
        and myroom_id=#{key} and content like '%' || #{word} ||'%'
    
        order by update_date desc
        ) a
    where rownum <= #{end}
    ) b
where b.rn > #{start}

select m.myroom_next_id myroomNextId, myroom_id myroomId, user_id, subject, content, reg_date, update_date, is_share isShare, g.group_id groupId
from myroom_next m, grouproom_hw g
where m.myroom_next_id=g.myroom_next_id and m.myroom_next_id=#{myroomNextId}
