mapper.select:
select c.*
from(select rownum rn, b.*
    from(select a.*, (select count(qna_comment_id) from qna_comment where qna_id=a.qna_id) commentcnt
        from (select q.qna_id, u.user_id, u.name, u.major_id, q.subject, q.content, q.reg_date, q.hit
                from qna q, users u
                where q.user_id = u.user_id and q.major_id = #{major_id}
                order by reg_date desc) a
        ) b
    where rownum <= #{end}
    ) c
where c.rn > #{start}
