mapper.update:
update notice
set hit = hit+1 
where notice_id = #{notice_id}

mapper.select:
select b.*
from(select rownum rn,a.* from (select notice_id,subject,content,is_notice,to_char(reg_date,'yy-mm-dd') reg_date,hit 
		from notice	
	
		where subject like '%'||#{word}||'%'
	
	
		
			order by reg_date asc) a
		
		
			order by notice_id asc) a
		
		
			order by reg_date desc) a
		

	where rownum <= #{end}
) b
where b.rn > #{start}

select b.*
from(select rownum rn,a.* from (select notice_id,subject,content,is_notice,to_char(reg_date,'yy-mm-dd') reg_date,hit 
from notice	where is_notice=1

	and subject like '%'||#{word}||'%'


	
		order by reg_date asc) a
	
	
		order by notice_id asc) a
	
	
		order by reg_date desc) a
	

where rownum <= #{end}
) b
where b.rn > #{start}
