mapper.select:
select b.*
from(select rownum rn,a.* from (select notice_id,subject,content,is_notice,to_char(reg_date,'yy-mm-dd') reg_date,reg_date rd,hit 
from notice   

	where subject like '%'||#{word}||'%'


	
		order by rd asc) a
	
	
		order by hit desc) a
	
	
		order by rd desc) a
	

where rownum <= #{end}
) b
where b.rn > #{start}

select b.*
from(select rownum rn,a.* from (select notice_id,subject,content,is_notice,to_char(reg_date,'yy-mm-dd') reg_date,reg_date rd,hit 
from notice   where is_notice=1

	and subject like '%'||#{word}||'%'


	
		order by rd asc) a
	
	
		order by hit desc) a
	
	
		order by rd desc) a
	

where rownum <= #{end}
) b
where b.rn > #{start}
