mapper.select:
select b.*
from(
        select rownum rn,a.* 
        from(
                select user_id,id,name,m.major_name,tel1,tel2,tel3,reg_date,is_block
                from users u,major m 
                where m.major_id=u.major_id 
                and is_block=0
                
                    
                        
                            and u.tel3 like '%'||#{word}||'%'
                        
                        
                            and u.name = #{word}
                        
                        
                            and u.id = #{word}
                        
                    
               

                
                    
                        
                            order by u.reg_date asc) a
                        
                        
                            order by u.name desc)a
                        
                        
                            order by u.user_id desc)a
                                                        
                    
                
                
                    order by u.id desc)a
                     
where rownum <= #{end}
) b
where b.rn > #{start}

select b.*
from(select rownum rn,a.* from(select user_id,id,name,m.major_name,tel1,tel2,tel3,reg_date,is_block
from users u,major m where m.major_id=u.major_id and is_block=1

    
        
            and u.tel3 like '%'||#{word}||'%'
        
        
            and u.name = #{word}
        
        
            and u.id = #{word}
        
    



    
        
            order by u.reg_date asc) a
        
        
            order by u.name desc)a
        
        
            order by u.user_id desc)a
                                        
    


    order by u.id desc)a
     
where rownum <= #{end}
) b
where b.rn > #{start}
